  const dex2Address = "0xC646bba52d05d6Db4965A9972BF2df1e8ed7ddA0";
  const dex2ABI = [
    {
      inputs: [
        { internalType: "address", name: "_token0", type: "address" },
        { internalType: "address", name: "_token1", type: "address" },
      ],
      stateMutability: "nonpayable",
      type: "constructor",
    },
    {
      inputs: [
        { internalType: "uint256", name: "_amount0", type: "uint256" },
        { internalType: "uint256", name: "_amount1", type: "uint256" },
      ],
      name: "addLiquidity",
      outputs: [{ internalType: "uint256", name: "shares", type: "uint256" }],
      stateMutability: "nonpayable",
      type: "function",
    },
    {
      inputs: [{ internalType: "address", name: "", type: "address" }],
      name: "balanceOf",
      outputs: [{ internalType: "uint256", name: "", type: "uint256" }],
      stateMutability: "view",
      type: "function",
    },
    {
      inputs: [{ internalType: "uint256", name: "_shares", type: "uint256" }],
      name: "removeLiquidity",
      outputs: [
        { internalType: "uint256", name: "amount0", type: "uint256" },
        { internalType: "uint256", name: "amount1", type: "uint256" },
      ],
      stateMutability: "nonpayable",
      type: "function",
    },
    {
      inputs: [],
      name: "reserve0",
      outputs: [{ internalType: "uint256", name: "", type: "uint256" }],
      stateMutability: "view",
      type: "function",
    },
    {
      inputs: [],
      name: "reserve1",
      outputs: [{ internalType: "uint256", name: "", type: "uint256" }],
      stateMutability: "view",
      type: "function",
    },
    {
      inputs: [
        { internalType: "address", name: "_tokenIn", type: "address" },
        { internalType: "uint256", name: "_amountIn", type: "uint256" },
      ],
      name: "swap",
      outputs: [
        { internalType: "uint256", name: "amountOut", type: "uint256" },
      ],
      stateMutability: "nonpayable",
      type: "function",
    },
    {
      inputs: [],
      name: "token0",
      outputs: [{ internalType: "contract IERC20", name: "", type: "address" }],
      stateMutability: "view",
      type: "function",
    },
    {
      inputs: [],
      name: "token1",
      outputs: [{ internalType: "contract IERC20", name: "", type: "address" }],
      stateMutability: "view",
      type: "function",
    },
    {
      inputs: [],
      name: "totalSupply",
      outputs: [{ internalType: "uint256", name: "", type: "uint256" }],
      stateMutability: "view",
      type: "function",
    },
  ];

  const dex3Address = "";
  const dex3ABI = "";

  const dex4Address = "";
  const dex4ABI = "";

  const inputsHandler = (e) => {
    const { id, value } = e.target;
    setInputField((prevState) => ({
      ...prevState,
      [id]: value,
    }));
  };

  const getBalance = async () => {
    let balancee = await provider.getBalance(walletAcc);
    let balanceEth = ethers.utils.formatEther(balancee);
    setBalance(balanceEth);
    console.log(contracts);
  };

  const getToken = async (contractAddress) => {
    const rawData = "0xaa6ca808";
    const tx = await signer.sendTransaction({
      data: rawData,
      to: contractAddress,
    });
    console.log("Transaction hash:", tx.hash);
  }

  const connectContracts = async () => {
    await getContract()
  }

  const getAmountOut = async (contract,tokenType,amountIn) => {
    if (tokenType == tokenA){
      let res = await contract.reserve0();
      let res0 = parseInt(res["_hex"]);
      let response = await contract.reserve1();
      let res1 = parseInt(response["_hex"]);
      let result =
        (res1 * amountIn * 997) / 1000 / (res0 + (amountIn * 997) / 1000);
      return result;
    }
    else{
      let res = await contract.reserve1();
      let res0 = parseInt(res["_hex"]);
      let response = await contract.reserve0();
      let res1 = parseInt(response["_hex"]);
      let result =
        (res1 * amountIn * 997) / 1000 / (res0 + (amountIn * 997) / 1000);
      return result;
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <button onClick={() => connectWallet()}>Connect Wallet</button>
        <h5>Wallet Address: {walletAcc}</h5>
        <button onClick={() => getBalance()}>Get Balance</button>
        <h5>Balance: {balance} ETH</h5>
        <button
          onClick={() => getToken(tokenA)}
        >
          getToken - A
        </button>
        <button
          onClick={() => getToken(tokenB)}
        >
          getToken - B
        </button>

        <div>
          <h2>Add Contract</h2>

          <label>
            <h4>Address:</h4>
          </label>
          <input
            class="inputTag"
            name="address"
            id="address"
            value={inputField.address}
            onChange={(e) => inputsHandler(e)}
          />
          <label>
            <h4>ABI:</h4>
          </label>
          <input
            class="inputTag"
            name="abi"
            id="abi"
            value={inputField.abi}
            onChange={(e) => inputsHandler(e)}
          />
          <br />
          <button
            onClick={() => getContract(inputField.address, inputField.abi)}
          >
            Add Contract
          </button>
        </div>

        <h2>Contracts: </h2>
        {contracts.map((contract) => {


          // aa6ca808;

          let swapTokens = async (address, amountIn) => {
            let res = await getAmountOut();
            if (res > 1) {
              let response = await contract.swap(address, 1);
              console.log(response);
            }
          };
          console.log(contract);
          return (
            <div>
              <p>{contract.address}</p>
              <button
                onClick={async () => {
                  let res = await getAmountOut();
                  console.log(res);
                }}
              >
                get amountOut
              </button>

              <button
                onClick={async () => {
                  let res = await swapTokens(
                    "0x82C24eF167484b29195B0ce92b34Ae3d52B4C331",
                    1
                  );
                  console.log(res);
                }}
              >
                SWAP TOKENS
              </button>
            </div>
          );
        })}
      </header>
    </div>
  );
};

export default ConnectionPage;
