import React, { useState } from "react";
import { useContext } from "react";
import WalletContext from "../utils/walletProvider";
import { Contract, ethers } from "ethers";
import "../App.css";

export const ConnectionPage = () => {
  let { connectWallet, walletAcc, provider, signer, getContract, contracts } =
    useContext(WalletContext);

  let [balance, setBalance] = useState(null);

  let [inputField, setInputField] = useState({
    address: "",
    abi: "",
  });

  const inputsHandler = (e) => {
    const { id, value } = e.target;
    setInputField((prevState) => ({
      ...prevState,
      [id]: value,
    }));
  };

  const getBalance = async () => {
    let balancee = await provider.getBalance(walletAcc);
    let balanceEth = ethers.utils.formatEther(balancee);
    setBalance(balanceEth);
    console.log(contracts);
  };

  return (
    <div className="App">
      <header className="App-header">
        <button onClick={() => connectWallet()}>Connect Wallet</button>
        <h5>Wallet Address: {walletAcc}</h5>
        <button onClick={() => getBalance()}>Get Balance</button>
        <h5>Balance: {balance} ETH</h5>
        <h2>Add Contract</h2>

        <label>
          <h4>Address:</h4>
        </label>
        <input
          class="inputTag"
          name="address"
          id="address"
          value={inputField.address}
          onChange={(e) => inputsHandler(e)}
        />
        <label>
          <h4>ABI:</h4>
        </label>
        <input
          class="inputTag"
          name="abi"
          id="abi"
          value={inputField.abi}
          onChange={(e) => inputsHandler(e)}
        />
        <button onClick={() => getContract(inputField.address, inputField.abi)}>
          Add Contract
        </button>

        <h2>Contracts: </h2>
        {contracts.map((contract) => {
          let getRes0 = async () => {
            let res = await contract.reserve0(); 
            let data = await res.j
          };
          console.log(contract);
          return (
            <div>
              <p>{contract.address}</p>
              <button onClick={() => console.log(getRes0())}></button>
            </div>
          );
        })}
      </header>
    </div>
  );
};

export default ConnectionPage;
