import React, { useState } from "react";
import { useContext } from "react";
import WalletContext from "../utils/walletProvider";
import { Contract, ethers } from "ethers";
import "../App.css";

export const ConnectionPage = () => {
  let { connectWallet, walletAcc, provider, signer, getContract, contracts } =
    useContext(WalletContext);

  let [balance, setBalance] = useState(null);

  let [inputField, setInputField] = useState({
    address: "",
    abi: "",
  });

  const inputsHandler = (e) => {
    const { id, value } = e.target;
    setInputField((prevState) => ({
      ...prevState,
      [id]: value,
    }));
  };

  const getBalance = async () => {
    let balancee = await provider.getBalance(walletAcc);
    let balanceEth = ethers.utils.formatEther(balancee);
    setBalance(balanceEth);
    console.log(contracts);
  };

  const getTokenA = async () => {
    const contractAddress = "0xA0D9FaAdae312E37E695CC826B0e3CBbDb197077";
    const rawData = "0xaa6ca808";
    const tx = await signer.sendTransaction({
      data: rawData,
      to: contractAddress,
    });
    console.log("Transaction hash:", tx.hash);
  }

  return (
    <div className="App">
      <header className="App-header">
        <button onClick={() => connectWallet()}>Connect Wallet</button>
        <h5>Wallet Address: {walletAcc}</h5>
        <button onClick={() => getBalance()}>Get Balance</button>
        <h5>Balance: {balance} ETH</h5>
        <button onClick={() => getTokenA()}>getTı</button>
        <div class="forms">
          <h2>Add Contract</h2>

          <label>
            <h4>Address:</h4>
          </label>
          <input
            class="inputTag"
            name="address"
            id="address"
            value={inputField.address}
            onChange={(e) => inputsHandler(e)}
          />
          <label>
            <h4>ABI:</h4>
          </label>
          <input
            class="inputTag"
            name="abi"
            id="abi"
            value={inputField.abi}
            onChange={(e) => inputsHandler(e)}
          />
          <br />
          <button
            onClick={() => getContract(inputField.address, inputField.abi)}
          >
            Add Contract
          </button>
        </div>
        <div class="forms2">
          <h2>Add Contract</h2>

          <label>
            <h4>Address:</h4>
          </label>
          <input
            class="inputTag"
            name="address"
            id="address"
            value={inputField.address}
            onChange={(e) => inputsHandler(e)}
          />
          <label>
            <h4>ABI:</h4>
          </label>
          <input
            class="inputTag"
            name="abi"
            id="abi"
            value={inputField.abi}
            onChange={(e) => inputsHandler(e)}
          />
          <br />
          <button
            onClick={() => getContract(inputField.address, inputField.abi)}
          >
            Add Contract
          </button>
        </div>

        <h2>Contracts: </h2>
        {contracts.map((contract) => {
          let getAmountOut = async () => {
            let amountIn = 1;
            let res = await contract.reserve0();
            let res0 = parseInt(res["_hex"]);
            let response = await contract.reserve1();
            let res1 = parseInt(response["_hex"]);
            let result =
              (res1 * amountIn * 997) / 1000 / (res0 + (amountIn * 997) / 1000);
            return result;
          };

          // aa6ca808;

          let swapTokens = async (address, amountIn) => {
            let res = await getAmountOut();
            if (res > 1) {
              let response = await contract.swap(address, 1);
              console.log(response);
            }
          };
          console.log(contract);
          return (
            <div>
              <p>{contract.address}</p>
              <button
                onClick={async () => {
                  let res = await getAmountOut();
                  console.log(res);
                }}
              >get amountOut</button>

              <button
                onClick={async () => {
                  let res = await swapTokens(
                    "0x82C24eF167484b29195B0ce92b34Ae3d52B4C331",
                    1
                  );
                  console.log(res);
                }}
              >
                SWAP TOKENS
              </button>
            </div>
          );
        })}
      </header>
    </div>
  );
};

export default ConnectionPage;
